function residence_and_rental_history_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('residence_and_rental_history');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	$data = [
		'tenant' => Request::lookup('tenant', ''),
		'address' => Request::val('address', ''),
		'landlord_or_manager_name' => Request::val('landlord_or_manager_name', ''),
		'landlord_or_manager_phone' => Request::val('landlord_or_manager_phone', ''),
		'monthly_rent' => Request::val('monthly_rent', ''),
		'duration_of_residency_from' => Request::dateComponents('duration_of_residency_from', ''),
		'to' => Request::dateComponents('to', ''),
		'reason_for_leaving' => Request::val('reason_for_leaving', ''),
		'notes' => Request::val('notes', ''),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('residence_and_rental_history', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		residence_and_rental_history_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

