function residence_and_rental_history_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('residence_and_rental_history', $selected_id, 'edit')) return false;

	$data = [
		'tenant' => Request::lookup('tenant', ''),
		'address' => Request::val('address', ''),
		'landlord_or_manager_name' => Request::val('landlord_or_manager_name', ''),
		'landlord_or_manager_phone' => Request::val('landlord_or_manager_phone', ''),
		'monthly_rent' => Request::val('monthly_rent', ''),
		'duration_of_residency_from' => Request::dateComponents('duration_of_residency_from', ''),
		'to' => Request::dateComponents('to', ''),
		'reason_for_leaving' => Request::val('reason_for_leaving', ''),
		'notes' => Request::val('notes', ''),
	];

	// get existing values
	$old_data = getRecord('residence_and_rental_history', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: residence_and_rental_history_before_update
	if(function_exists('residence_and_rental_history_before_update')) {
		$args = ['old_data' => $old_data];
		if(!residence_and_rental_history_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'residence_and_rental_history', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="residence_and_rental_history_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	update_calc_fields('residence_and_rental_history', $data['selectedID'], calculated_fields()['residence_and_rental_history']);

	// hook: residence_and_rental_history_after_update
	if(function_exists('residence_and_rental_history_after_update')) {
		if($row = getRecord('residence_and_rental_history', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!residence_and_rental_history_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('residence_and_rental_history', $selected_id);
}

